class Persona:
 def __init__(self, nombre, edad):
 self.nombre = nombre
 self.edad = edad

 def saludar(self):
 print(f"Hola, mi nombre es {self.nombre} y tengo {self.edad} años.")

persona = Persona("Juan", 25)
persona.saludar() # Salida: Hola, mi nombre es Juan y tengo 25 años.

